    private void btnSacarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSacarActionPerformed
            String ValorSaque = SacarText.getText();
            try {
                double valor = Double.parseDouble(ValorSaque);
                if (valor > 0) {
                    String cpf = ControleLogin.getCpfUsuarioLogado();
                    String resultado = Controle.sacar(valor, cpf);
                    JOptionPane.showMessageDialog(null, resultado);
                    if (resultado.equals("Saque realizado com sucesso!")) {
                        atualizarTextAreaComSaldos(cpf);
                    }
                } else {
                    JOptionPane.showMessageDialog(null, "Por favor, insira um valor positivo.");
                }
            } catch (NumberFormatException ex) {
                JOptionPane.showMessageDialog(null, "Por favor, insira um valor v√°lido.");
            }
     
    }//GEN-LAST:event_btnSacarActionPerformed
    private void atualizarTextAreaComSaldos(String cpf) {
    Map<String, Double> saldos = Controle.obterSaldos(cpf);
    String texto = String.format(
        "Saldo em Reais: R$ %.2f\n" +
        "Saldo em Bitcoin: %.5f BTC\n" +
        "Saldo em Ethereum: %.5f ETH\n" +
        "Saldo em Ripple: %.5f XRP",
        saldos.get("Reais"),
        saldos.get("Bitcoin"),
        saldos.get("Ethereum"),
        saldos.get("Ripple")
    );
    SacarArea.setText(texto);
    SacarArea.setEditable(false);
}

